import { GET_ITEMS, ADD_ITEM, DELETE_ITEM } from '../actions/types';

const initialState = {
  artifacts:[]
}

export default function(state = initialState, action){
  switch (action.type) {
    case GET_ITEMS:
      return{
        ...state
      }
      case DELETE_ITEM:
      return{
        ...state,
        artifacts: state.artifacts.filter(item => item.id !== id)
      }
    default:
      return state;


  }
}
