/**
 * User Modal pop up
 * Created for IT PROJECT 2019
 * Group : Odin's Army
 * Initial commit/file creation by Xiaoxuan Li - ID: 933206, Sep 21, 2019
 **/

import React, { Component } from 'react';
import {Modal, Button, Row, Col, Form} from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";

export class UserModal extends Component{

  constructor(props){
    super(props);

    this.state ={
      username:'',
      password:'',
    }
  }

  onSubmit(e){
    e.preventDefault();

  }

  render(){
     return(
       <Modal
      {...this.props}
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Log In
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="container">
          <form onSubmit={this.onSubmit}>
            <div className="form-group">
              <label>User Name: </label>
              <input  type="text"
                  required
                  className="form-control"
                  value={this.state.name}
                  onChange={this.onChangeName}
                  />
            </div>
          </form>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={this.props.onHide} className="btn btn-outline-info" style={{backgroundColor:"#93c6c8"}}>Create New Account</Button>
      </Modal.Footer>
    </Modal>
     )
  }

}
